pragma solidity ^0.6.0;

import {SafeMath} from '@openzeppelin/contracts/math/SafeMath.sol';
import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import {ReentrancyGuardUpgradeSafe} from '@openzeppelin/contracts-ethereum-package/contracts/utils/ReentrancyGuard.sol';
import {IInvestmentIntegration} from "./IInvestmentIntegration.sol";
import {StandardERC20} from "../libs/StandardERC20.sol";
import {AbstractHonestContract} from "../AbstractHonestContract.sol";
import {IHonestConfiguration} from '../interfaces/IHonestConfiguration.sol';

interface yTokenV2 {
    function deposit(uint _amount) external;

    function withdraw(uint _shares) external;

    function getPricePerFullShare() external view returns (uint);
}

contract YearnV2Integration is IInvestmentIntegration, AbstractHonestContract, ReentrancyGuardUpgradeSafe {

    using SafeERC20 for IERC20;
    using SafeMath for uint;
    using StandardERC20 for ERC20;

    address private _honestConfiguration;

    function initialize(address honestConfiguration) external initializer() {
        require(honestConfiguration != address(0), 'YearnV2Integration.initialize: honestConfiguration address must be valid');

        super.initialize();
        __ReentrancyGuard_init();
        _honestConfiguration = honestConfiguration;
    }

    function invest(address account, address asset, uint amount) external override onlyVault nonReentrant returns (uint) {
        require(account != address(0), "YearnV2Integration.invest: account must be valid");
        require(amount > 0, "YearnV2Integration.invest: invest must be greater than 0");

        IERC20(asset).safeTransferFrom(account, address(this), amount);

        address yToken = _contractOf(asset);
        uint before = IERC20(yToken).balanceOf(address(this));
        IERC20(asset).safeApprove(yToken, amount);
        yTokenV2(yToken).deposit(amount);

        uint credits = IERC20(yToken).balanceOf(address(this)).sub(before);
        return ERC20(yToken).standardize(credits);
    }

    function collect(address account, address asset, uint credits) external override onlyVault nonReentrant returns (uint) {
        require(account != address(0), "YearnV2Integration.collect: account must be valid");
        require(credits > 0, "YearnV2Integration.collect: credits must be greater than 0");

        yTokenV2 yToken = yTokenV2(_contractOf(asset));
        uint originShares = ERC20(address(yToken)).resume(credits);
        uint amount = originShares.mul(yToken.getPricePerFullShare()).div(uint(1e18));
        yToken.withdraw(originShares);

        require(amount <= IERC20(asset).balanceOf(address(this)), "YearnV2Integration.collect: insufficient balance");
        IERC20(asset).safeTransfer(account, amount);

        return ERC20(asset).standardize(amount);
    }

    function priceOf(address asset) external override view returns (uint) {
        address yToken = _contractOf(asset);
        return yTokenV2(yToken).getPricePerFullShare();
    }

    function shareOf(address asset) public override view returns (uint) {
        address yToken = _contractOf(asset);
        return ERC20(yToken).standardBalanceOf(address(this));
    }

    function shares() external override view returns (address[] memory, uint[] memory, uint, uint) {
        address[] memory assets = IHonestConfiguration(_honestConfiguration).activeBasketAssets();
        uint[] memory credits = new uint[](assets.length);
        uint totalShare;
        uint totalBalance;
        for (uint i = 0; i < assets.length; ++i) {
            credits[i] = shareOf(assets[i]);
            totalShare = totalShare.add(credits[i]);
            totalBalance = totalBalance.add(_balanceOf(assets[i]));
        }
        return (assets, credits, totalShare, totalBalance.mul(uint(1e18)).div(totalShare));
    }

    function balanceOf(address asset) external override view returns (uint) {
        return _balanceOf(asset);
    }

    function balances() external override view returns (address[] memory, uint[] memory, uint) {
        address[] memory assets = IHonestConfiguration(_honestConfiguration).activeBasketAssets();
        uint[] memory aBalances = new uint[](assets.length);
        uint totalBalances;
        for (uint i = 0; i < assets.length; ++i) {
            aBalances[i] = _balanceOf(assets[i]);
            totalBalances = totalBalances.add(aBalances[i]);
        }
        return (assets, aBalances, totalBalances);
    }

    function totalBalance() external override view returns (uint) {
        address[] memory assets = IHonestConfiguration(_honestConfiguration).activeBasketAssets();
        uint balance;
        for (uint i = 0; i < assets.length; ++i) {
            balance = balance.add(_balanceOf(assets[i]));
        }
        return balance;
    }

    function _contractOf(address asset) internal view returns (address) {
        return IHonestConfiguration(_honestConfiguration).basketAssetInvestmentIntegration(asset);
    }

    function _balanceOf(address _asset) internal view returns (uint) {
        address yToken = _contractOf(_asset);

        uint credits = ERC20(yToken).standardBalanceOf(address(this));
        if (credits == 0) {
            return 0;
        }
        return yTokenV2(yToken).getPricePerFullShare().mul(credits).div(uint(1e18));
    }
}","keccak256":"0xaee764ca1918d48685ef0e413057da7a2870f45659a2cfb63e7a9a7cb7412941","license":"LGPL-3.0-or-later"},"contracts/interfaces/IHonestConfiguration.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-or-later

pragma solidity ^0.6.0;

import {IAssetPriceIntegration} from '../integrations/IAssetPriceIntegration.sol';

interface IHonestConfiguration {

    function honestAsset() external view returns (address);

    function basketAssets() external view returns (address[] memory, bool[] memory);

    function activeBasketAssets() external view returns (address[] memory);

    function basketAssetInvestmentIntegration(address asset) external view returns (address);

    function swapFeeRate() external view returns (uint);

    function redeemFeeRate() external view returns (uint);

    function claimableRewardsPercentage() external view returns (uint);

    function addBasketAsset(address asset, address bAssetInvestment) external;

    function removeBasketAsset(address asset) external;

    function activateBasketAsset(address asset) external;

    function deactivateBasketAsset(address asset) external;

    function setSwapFeeRate(uint feeRate) external;

    function setRedeemFeeRate(uint feeRate) external;

    function setClaimableRewardsPercentage(uint percentage) external;
}","keccak256":"0xedc2dffa10254d4d08690346e3a598e5c80c36898c971bfe33835770b9984747","license":"LGPL-3.0-or-later"},"contracts/libs/StandardERC20.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-or-later

pragma solidity ^0.6.0;

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import {SafeMath} from '@openzeppelin/contracts/math/SafeMath.sol';

/**
 * @dev Standard ERC20 contract functions, support 18 decimals ERC20 token
 *
 * use this library add `using StandardERC20 for ERC20;` statement to your contract
 */
library StandardERC20 {

    using SafeERC20 for IERC20;
    using SafeMath for uint;

    uint8 private constant _standardDecimals = 18;

    function standardBalanceOf(ERC20 token, address account) internal view returns (uint) {
        uint balance = token.balanceOf(account);
        return standardize(token, balance);
    }

    function standardApprove(ERC20 token, address to, uint standardAmount) internal {
        return IERC20(token).safeApprove(to, resume(token, standardAmount));
    }

    function standardTransfer(ERC20 token, address to, uint standardAmount) internal {
        return IERC20(token).safeTransfer(to, resume(token, standardAmount));
    }

    function standardTransferFrom(ERC20 token, address from, address to, uint standardAmount) internal {
        return IERC20(token).safeTransferFrom(from, to, resume(token, standardAmount));
    }

    function standardize(ERC20 token, uint value) internal view returns (uint) {
        uint8 decimals = token.decimals();
        require(decimals <= _standardDecimals, 'illegal decimals');
        if (decimals == _standardDecimals) {
            return value;
        }
        return value.mul(uint(10) ** uint(_standardDecimals - decimals));
    }

    function resume(ERC20 token, uint value) internal view returns (uint) {
        uint8 decimals = token.decimals();
        require(decimals <= _standardDecimals, 'illegal decimals');
        if (decimals == _standardDecimals) {
            return value;
        }
        return value.div(uint(10) ** uint(_standardDecimals - decimals));
    }
}","keccak256":"0x3c14dbd452250ed40efb7dd7bfc068f295d3096e7c439ae0eeefefd28dbcc718","license":"LGPL-3.0-or-later"},"contracts/mock/MockYTokenV2.sol":{"content":"// SPDX-License-Identifier: LGPL-3.0-or-later

pragma solidity ^0.6.0;

import {IERC20} from '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import {ERC20} from '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import {Ownable} from '@openzeppelin/contracts/access/Ownable.sol';
import {SafeERC20} from '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import {SafeMath} from '@openzeppelin/contracts/math/SafeMath.sol';

import {yTokenV2} from "../integrations/YearnV2Integration.sol";

abstract contract MockYTokenV2 is yTokenV2, ERC20, Ownable {

    using SafeERC20 for IERC20;
    using SafeMath for uint;

    IERC20 private _token;

    function mint(address account, uint amount) external onlyOwner returns (bool) {
        _mint(account, amount);
        return true;
    }

    function burn(address account, uint amount) external onlyOwner returns (bool) {
        _burn(account, amount);
        return true;
    }

    function deposit(uint _amount) external override {
        _token.safeTransferFrom(_msgSender(), address(this), _amount);
        uint shares = _amount.mul(uint(1e18)).div(getPricePerFullShare());
        _mint(_msgSender(), shares);
    }

    function withdraw(uint _shares) external override {
        _transfer(_msgSender(), address(this), _shares);
        _token.safeTransfer(_msgSender(), _shares.mul(getPricePerFullShare()).div(uint(1e18)));
    }

    function getPricePerFullShare() public override view returns (uint) {
        return block.timestamp.sub(uint(1599170000)).mul(uint(1e12));
    }

    function _setToken(address _tokenAddress) internal {
        _token = IERC20(_tokenAddress);
    }
}

contract MockYDAI is MockYTokenV2 {

    constructor(address _dai) public ERC20('Mock yDAI', 'yDAI') {
        _setToken(_dai);
    }
}

contract MockYUSDT is MockYTokenV2 {

    constructor(address _usdt) public ERC20('Mock yUSDT', 'yUSDT') {
        _setToken(_usdt);
        _setupDecimals(6);
    }
}

contract MockYUSDC is MockYTokenV2 {

    constructor(address _usdc) public ERC20('Mock yUSDC', 'yUSDC') {
        _setToken(_usdc);
        _setupDecimals(6);
    }
}

contract MockYTUSD is MockYTokenV2 {

    constructor(address _tusd) public ERC20('Mock yTUSD', 'yTUSD') {
        _setToken(_tusd);
    }
}","keccak256":"0xf68e88accee54c39b1682383821dee9599e2f7d60a2b461d4564be587a4698d4","license":"LGPL-3.0-or-later"}},"version":1}
